    }

    private void AddSubTask(TaskItem parentTask)
    {
        if (!string.IsNullOrWhiteSpace(newTaskTitle) && !string.IsNullOrWhiteSpace(newTaskDescription))
        {
            if (parentTask.SubTasks == null)
